         WHEN IdleHit => 
            IF ((miss = '1') and
                (dirty = '1')) THEN 
               next_state <= writeBack;
            ELSIF ((miss = '1') and
                   (dirty = '0')) THEN 
               next_state <= Load;
            ELSE
               next_state <= IdleHit;
            END IF;
         WHEN writeBack => 
            IF (pmresp_h = '1') THEN 
               next_state <= Load;
            ELSE
               next_state <= writeBack;
            END IF;
